function GetLine(const S: string; LineNumber: Integer): string;
var
  I, Count: Integer;
  StartPos: Integer;
begin
  Count := 1;
  StartPos := 1;

  for I := 1 to Length(S) do
  begin
    if S[I] = #10 then
    begin
      if Count = LineNumber then
      begin
        Result := Trim(Copy(S, StartPos, I - StartPos));
        Exit;
      end;

      Count := Count + 1;
      StartPos := I + 1;
    end;
  end;

  if Count = LineNumber then
    Result := Trim(Copy(S, StartPos, Length(S) - StartPos + 1))
  else
    Result := '';
end;

{ Auto-generated function. Remove this tag to customize. }
function GetLineCount(const S: string): Integer;
var
  I: Integer;
begin
  Result := 1;
  for I := 1 to Length(S) do
  begin
    if S[I] = #10 then
      Result := Result + 1;
  end;
end;

