procedure InitializeWizard;
begin
  if NeedsDotNet then
  begin
    WizardForm.StatusLabel.Caption := 'Downloading .NET Runtime...';
    idpDownloadFile('{RuntimeDownloadUrl}/{RuntimeVersion}/{RuntimeInstallerName}',ExpandConstant('{tmp}\\{RuntimeInstallerName}'));
  end;
end;

{ Auto-generated function. Remove this tag to customize. }
function IsDotnetRuntimeInstalled(MajorVersion: Integer): Boolean;
var
  Output, Line: string;
  I: Integer;
begin
  Result := False;

  Log('IsDotnetRuntimeInstalled...');

  if not ExecAndCaptureOutput('dotnet', '--list-runtimes', Output) then
  begin
    Log(' Could not capture output from dotnet --list-runtimes');
    Exit;
  end;

  I := 1;
  while I <= GetLineCount(Output) do
  begin
    Line := GetLine(Output, I);

    Log('Reading line: ' + Line);

    if Pos('Microsoft.WindowsDesktop.App ' + IntToStr(MajorVersion) + '.', Line) = 1 then
    begin
      Log(' Line found...');
      Result := True;
      Exit;
    end;

    I := I + 1;
  end;
end;

{ Auto-generated function. Remove this tag to customize. }
function NeedsDotNet(): Boolean;
var
  Major: Integer;
begin
  Log('NeedsDotNet...');

  Major := ExtractMajorVersionFromInstaller('{RuntimeInstallerName}');
  Log(Format(' Major .NET Version: %d', [Major]));

  if Major = 0 then
  begin
    Log('Error: Unable to determine .NET major version from installer name: {RuntimeInstallerName}');
    Result := True;
    Exit;
  end;

  Result := not IsDotnetRuntimeInstalled(Major);
end;

{ Auto-generated function. Remove this tag to customize. }
function PosEx(const SubStr, S: string; Offset: Integer): Integer;
var
  I: Integer;
begin
  Result := 0;

  if Offset < 1 then
    Offset := 1;

  for I := Offset to (Length(S) - Length(SubStr) + 1) do
  begin
    if Copy(S, I, Length(SubStr)) = SubStr then
    begin
      Result := I;
      Exit;
    end;
  end;
end;
